mapper.select:
select s.*, c.name clazzName from student s left join clazz c on s.clazz_id = c.id
s.name like concat('%',#{name},'%')
and s.clazz_id = #{clazzId}
and s.degree = #{degree}
order by s.update_time desc

mapper.update:
update student
name=#{name}, no=#{no}, gender=#{gender}, phone=#{phone}, id_card=#{idCard}, is_college=#{isCollege}, address=#{address}, graduation_date=#{graduationDate}, degree=#{degree}, violation_count=#{violationCount}, violation_score=#{violationScore}, clazz_id=#{clazzId}, update_time=#{updateTime},
where id=#{id}
